INSERT INTO `service_information` (`id`, `name`, `description`, `icon`, `image`, `parent`, `status`, `created_at`, `updated_at`) VALUES
(6, 'WEB DESIGN', '<p style=\"text-align: justify;\"><span style=\"font-size: 12pt; font-family: \'times new roman\', times, serif; color: #ffffff;\"><strong>Custom Application Development Services</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: 12pt; font-family: \'times new roman\', times, serif; color: #ffffff;\">We rely on our technological expertise and specialized industry experience to develop any type of web, mobile, desktop, and hybrid app per your business requirements.</span></p>', 'uploads/5f51f0f645c70graphic-design (6).png', 'uploads/61b75d67e7990WhatsApp Image 2021-12-13 at 8.48.15 PM.jpeg', 10, 1, '2020-09-03 03:45:46', '2021-12-15 07:32:20'),
(8, 'GRAPHIC DESIGN', '<p style=\"text-align: justify;\"><span style=\"color: #ffffff; font-size: 12pt; font-family: \'times new roman\', times, serif;\">&nbsp;VIDEO CREATION</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #ffffff; font-size: 12pt; font-family: \'times new roman\', times, serif;\">&nbsp;MOTION GRAPHICS</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #ffffff; font-size: 12pt; font-family: \'times new roman\', times, serif;\">&nbsp;LOGO CREATION/ GIF</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #ffffff; font-size: 12pt; font-family: \'times new roman\', times, serif;\">&nbsp;ANIMATION VIDEO</span></p>', 'uploads/5f51f6234d3f7pen.png', 'uploads/61b851666782aGraphics Desing.jpeg', 10, 1, '2020-09-03 03:46:55', '2021-12-15 07:32:01'),
(9, 'CYBER SECURITY', '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\"><strong>QA and Software Testing Services</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">Comprehensive quality assurance is built into our custom software service model, but we can also provide on-demand QA and a suite of functional and usability software tests upon request.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\"><strong>IT Security Services</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">Our thorough threat audits help us identify your software infrastructure\'s most pressing vulnerabilities, allowing us to integrate the encryptions, security services and access protocols you require.</span></p>', 'uploads/5f51f616c48f0internet.png', 'uploads/61b851955bc13Cyber Security.jpeg', 10, 1, '2020-09-03 03:47:20', '2021-12-15 07:31:39'),
(11, 'SEO AND DATA  ANALYTICS', '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">&nbsp;SEARCH ENGINE OPTIMIZATION</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">&nbsp;MARKET ANALYSIS</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">&nbsp;MARKET INTELLIGENCE</span></p>', 'uploads/5f51f57da1169statistics.png', 'uploads/61b851c74bdf0SEO.jpeg', 10, 1, '2020-09-03 03:48:21', '2021-12-15 07:31:06'),
(12, 'APP DEVELOPMENT', '<p style=\"text-align: justify;\"><span style=\"color: #ffffff; font-size: 12pt;\"><strong><span style=\"font-family: \'times new roman\', times, serif;\">App Maintenance Services</span></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">Our application maintenance and modernization services are designed to ensure the scalability, performance, and sustainability of your entire software infrastructure as your business grows.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #ffffff; font-size: 12pt;\"><strong><span style=\"font-family: \'times new roman\', times, serif;\">API Integration Services</span></strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">We build and implement custom APIs for all breeds of applications. helping to add functionality to your software systems and facilitate communication between your apps and others.</span></p>', 'uploads/5f51f54b20de2android.png', 'uploads/5f50bc0229f03app.jpg', 10, 1, '2020-09-03 03:48:50', '2021-12-15 07:30:43'),
(15, 'DIGITAL MARKETING', '<p style=\"text-align: justify;\"><span style=\"color: #999999; font-family: \'times new roman\', times, serif; font-size: 12pt;\">&nbsp;<span style=\"color: #ffffff;\">FACEBOOK AD</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #ffffff; font-family: \'times new roman\', times, serif; font-size: 12pt;\">&nbsp;GOOGLE AD</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #ffffff; font-family: \'times new roman\', times, serif; font-size: 12pt;\">&nbsp;YOUTUBE CAMPAIGN</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #ffffff; font-family: \'times new roman\', times, serif; font-size: 12pt;\">&nbsp;LINKEDIN CAMPAIGN</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #ffffff; font-family: \'times new roman\', times, serif; font-size: 12pt;\">&nbsp;INSTAGRAM CAMPAIGN</span></p>', 'uploads/5f51f4b01a997skills (1).png', 'uploads/61b85137eb5daDigital Marketing.jpeg', 10, 1, '2020-09-03 03:49:55', '2021-12-15 07:30:23'),
(19, 'FOOD , LEATHER AND JUTE PRODUCTS', '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">TEXTILE AND GARMENTS PRODUCTS</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">FOOD PRODUCTS</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">LEATHER PRODUCTS</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">JUTE PRODUCTS</span></p>', 'uploads/61b885c5e1d32Food Leather Product.png', 'uploads/61b885c5e1f46Leather Goods.jpeg', 14, 1, '2020-09-03 03:54:33', '2021-12-15 07:30:01'),
(20, 'CLOUD SERVICES', '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">Key Features of a Cloud Management Platform</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">Cost Control: Understand where costs come from.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">Performance Tuning: Spend where you need to and redistribute resources</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">Real-time Decision Making: Having real-time data you can use to predict and need to make</span><br /><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">decisions.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">Predict, Plan and Ensure Resources: Using stats on cloud data usage, you can estimate the need for the future and negotiate a discount or pre-allocate resources</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">Automation Provisioning and De-provisioning: A code is written to provision and de-provision services, which automates the process for future use.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"color: #ffffff; font-size: 12pt;\"><span style=\"font-family: \'times new roman\', times, serif;\">Simplicity: Using the Infrastructure-as-a-Code approach, you can migrate your infrastructure to the </span><span style=\"font-family: \'times new roman\', times, serif;\">cloud from existing providers in a simple and efficient manner</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">Privacy: WisCLOUD can also be integrated into private cloud environments Team Management: WisCLOUD is the platform that can enable file sharing with the team at any time, including chatbox, voice, and video call, etc.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">Data Backup and Recovery Services: We implement robust data backup and recovery strategies for cloud-based, on-premises, and hybrid servers, designed to ensure the integrity of your data and the continuity of your business.</span></p>', 'uploads/5f51f3d61321baggregate.png', 'uploads/61b8619634f8eCloud.jpeg', 10, 1, '2020-09-03 03:58:19', '2021-12-15 07:23:40'),
(21, 'ENVIRONMENTAL ECO SYSTEM PRODUCTS', '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\"><strong>Wisdom Valley is working with multiple types of trees in Bangladesh</strong></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">Wisdom Valley gives environmentally responsible companies a way to integrate tree planting into their business activities and give back to the natural world, making sure that they are truly \'Green Businesses\'. This gives their customers the opportunity to buy products and services that help to make a greener world. Wisdom Valley is working with multiple types of trees in Bangladesh as required by private and government entities.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">Apart from the plants, green towers are also to be developed in the smaller spaces on the land. A proper irrigation system is also being developed on the land where the plants will be grown.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">Wisdom Valley has conducted 50,000 tree plantations of over 20 different species over empty land in various zones of the country.</span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>', 'uploads/61b8856327ea4Eco Product.png', 'uploads/61b8856327f72ENVIRONMENTAL ECO SYSTEM PRODUCTS.jpeg', 14, 1, '2020-09-03 03:58:50', '2021-12-15 07:28:50'),
(22, 'IOT DEVICES', '<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\"><span style=\"font-weight: 400;\">IOT devices are nonstandard computing devices that connect wirelessly to a network and have the ability to transmit data, such as the many devices on the internet of things</span><span style=\"font-weight: 400;\">.</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\"><span style=\"font-weight: 400;\">Examples: Smart Mobiles, smart refrigerators, smartwatches, smart fire alarms, smart door locks, smart bicycles, medical sensors, fitness trackers, smart security systems.</span></span></p>', 'uploads/61b883fcc5e85iot.png', 'uploads/61b883fcc5f6dIOT Devices.jpeg', 14, 1, '2020-09-03 03:59:12', '2021-12-15 07:28:27'),
(23, 'NETWORK INFRASTRUCTURE', '<p style=\"text-align: justify;\"><span style=\"font-size: 12pt; font-family: \'times new roman\', times, serif; color: #ffffff;\">Wisdom Valley is not only equipped with a pool of talent where manpower is concerned, but it also has that infrastructure that assists them to achieve their targets efficiently. The company is equipped with the latest hardware and other network-related peripherals and uses up-to-date software for its efficient functioning. </span></p>', 'uploads/61b8848b693cdNETWORK INFRASTRUCTURE.png', 'uploads/61b8848b69511NETWORK INFRASTRUCTURE.jpeg', 14, 1, '2020-09-03 03:59:27', '2021-12-15 07:28:11'),
(24, 'NANO TECHNOLOGY', '<p style=\"text-align: justify;\"><span style=\"font-weight: 400; font-family: \'times new roman\', times, serif; font-size: 12pt; color: #ffffff;\">Nanotechnology is the manipulation of matter on a near-atomic scale to produce new structures, materials, and devices. Nanotechnology is generally defined as engineered structures, devices, and systems. Nanomaterials are defined as those things that have a length scale between 1 and 100 nanometers.&nbsp;</span></p>', 'uploads/61b879e65e984nanotechnology.png', 'uploads/61b879e65f1e1Nano Tech.jpeg', 14, 1, '2020-09-03 03:59:40', '2021-12-15 07:27:55'),
